import React, { useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';

function WorkerEvaluation() {
  let id = useParams();
  const [workers, setWorkers] = useState();


  useEffect(() => {
    Axios.get(`http://localhost:5050/workers`).then((response) => {
      setWorkers(response.data);
    });
  }, []);
  let navigate = useNavigate();


  const goTo = (path) => {
    navigate(path);
  }

  return (
    <div>
      <h1>Worker List</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {workers.map((worker) => (
            <tr key={worker.id}>
              <td>{worker.id}</td>
              <td>{worker.name}</td>
              <td>
                <button className="btn btn-primary" onClick={() => goTo(`/evaluationPage/${id}`)}>
                  Evaluate Employee
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );


}

export default WorkerEvaluation;
